import React, { Component } from "react";

export default class LItem extends Component {
    //勾选或取消勾选
    handleChangeDone=(id)=>{
        return (event)=>{
            console.log("handleChangeDone:",id,event.target)
        }
    }
  render() {
    const {id, done,category, content} = this.props
    return (
      <div  className={`todo-item ${done && "done"}`}>
        <label>
          <input type="checkbox" defaultChecked={done} onChange={this.handleChangeDone(id)} />
          <span className={category ==='business'?'bubble business':'bubble personal'}></span>
        </label>

        <div className="todo-content">
          <input type="text" value={content} readOnly></input>
        </div>

        <div className="actions">
          <button className="delete">Delete</button>
        </div>
      </div>
    );
  }
}
