import React, { Component } from "react";
import axios from "axios";
import PubSub from "pubsub-js";
import "./index.css";

export default class Search extends Component {
  handleSearchUsers = () => {
    //获取用户输入
    // 连续结构赋值 keywordRef 不可用
    const {
      keywordRef: { value },
    } = this;

    // 发送请求前通知List更新状态
    PubSub.publish("updateSearchState",{ isFirst: false, isLoading: true, error: "" });
    // 发送网络请求
    axios.get(`https://api.github.com/search/users?wq=${value}`).then(
      (response) => {
        //通知List组件设置user data
        PubSub.publish("updateSearchState",{
          userLst: response.data.items,
          isLoading: false,
        });
      },
      (error) => {
        console.log("error!!", error);
        // 请求失败后通知List更新状态
        PubSub.publish("updateSearchState",{ error: error.message, isLoading: false });
      }
    );
  };
  handleOnKeyUp = (event) => {
    // console.log(event.keyCode)
    // 当用户输入回车键时，触发搜索按钮事件
    if (event.keyCode === 13) {
      this.handleSearchUsers();
    }
  };
  render() {
    return (
      <section className="jumbotron">
        <h3 className="jumbotron-heading">Search Github Users</h3>
        <div>
          <input
            type="text"
            placeholder="Please enter name to start searching"
            ref={(c) => (this.keywordRef = c)}
            onKeyUp={this.handleOnKeyUp}
          />
          &nbsp;
          <button onClick={this.handleSearchUsers} className="search-button">
            Search
          </button>
        </div>
      </section>
    );
  }
}
