import React, { Component } from "react";

export default class Header extends Component {
  contentRef = React.createRef();
  state = { content: "", category: "personal" };
  addToDoItem = (event) => {
    event.preventDefault();
    let { contentRef } = this;
    const { category } = this.state;
    console.log("addToDoItem:", contentRef.current.value, category);
    contentRef = "";
  };
  handleChangeData = (type, event) => {
    console.log("handleChangeDate:",type, event.target.value)
    if (type === "content") this.setState({ content: event.target.value });
    if (type === "category") this.setState({ category: event.target.value });
  };
  render() {
    // console.log("render",this.state.category)
    return (
      <div>
        <section className="greeting">
          <h2 className="title">
            What's up,{" "}
            <input
              type="text"
              id="name"
              placeholder="Name here"
              value="Miranda"
              readOnly
            />
          </h2>
        </section>
        <section className="create-todo">
          <h3>CREATE A TODO</h3>

          <form id="new-todo-form" onSubmit={this.addToDoItem}>
            <h4>What's on your todo list?</h4>
            <input
              type="text"
              name="content"
              id="content"
              placeholder="e.g. make a video"
              onBlur={(event) => this.handleChangeData("content", event)}
            />

            <h4>Pick a category</h4>
            <div className="options">
              <label>
                <input
                  type="radio"
                  name="category"
                  id="category1"
                  value="business"
                  checked={this.state.category === "business" ? true : false}
                  onChange={(event) => this.handleChangeData("category", event)}
                />
                <span className="bubble business"></span>
                <div>Business</div>
              </label>

              <label>
                <input
                  type="radio"
                  name="category"
                  id="category2"
                  value="personal"
                  checked={this.state.category === "personal" ? true : false}
                  onChange={(event) => this.handleChangeData("category", event)}
                />
                <span className="bubble personal"></span>
                <div>Personal</div>
              </label>
            </div>

            <input type="submit" value="Add todo" />
          </form>
        </section>
      </div>
    );
  }
}
