import React, { Component } from "react";

export default class Header extends Component {
  contentRef = React.createRef();
  categoryRef = React.createRef("personal");
  addToDoItem = (event) => {
    event.preventDefault();
    const { contentRef, categoryRef } = this;
    console.log(contentRef, categoryRef);
  };
  componentDidMount() {
    this.categoryRef = "personal";
  }
  render() {
    const { contentRef, categoryRef } = this;
    console.log(contentRef, categoryRef);
    return (
      <div>
        <section className="greeting">
          <h2 className="title">
            What's up,{" "}
            <input
              type="text"
              id="name"
              placeholder="Name here"
              value="Miranda"
              readOnly
            />
          </h2>
        </section>
        <section className="create-todo">
          <h3>CREATE A TODO</h3>

          <form id="new-todo-form" onSubmit={this.addToDoItem}>
            <h4>What's on your todo list?</h4>
            <input
              type="text"
              name="content"
              id="content"
              placeholder="e.g. make a video"
              ref={this.contentRef}
            />

            <h4>Pick a category</h4>
            <div className="options">
              <label>
                <input
                  type="radio"
                  name="category"
                  id="category1"
                  value="business"
                  ref={this.categoryRef}
                />
                <span className="bubble business"></span>
                <div>Business</div>
              </label>

              <label>
                <input
                  type="radio"
                  name="category"
                  id="category2"
                  value="personal"
                  ref={this.categoryRef}
                />
                <span className="bubble personal"></span>
                <div>Personal</div>
              </label>
            </div>

            <input type="submit" value="Add todo" />
          </form>
        </section>
      </div>
    );
  }
}
