import React, { Component } from "react";

export default class Header extends Component {
  contentRef = React.createRef();
  state={category:"personal"}
  addToDoItem = (event) => {
    event.preventDefault();
    const { contentRef} = this;
    const {category} = this.state
    console.log("addToDoItem:",contentRef.current.value, category);
  };
  handleChangeCategory=(event)=>{
    console.log("handleChangeCategory",event.target.value)
    this.setState({category: event.target.value})
  }
  render() {
    console.log("render",this.state.category)
    return (
      <div>
        <section className="greeting">
          <h2 className="title">
            What's up,{" "}
            <input
              type="text"
              id="name"
              placeholder="Name here"
              value="Miranda"
              readOnly
            />
          </h2>
        </section>
        <section className="create-todo">
          <h3>CREATE A TODO</h3>

          <form id="new-todo-form" onSubmit={this.addToDoItem}>
            <h4>What's on your todo list?</h4>
            <input
              type="text"
              name="content"
              id="content"
              placeholder="e.g. make a video"
              ref={this.contentRef}
            />

            <h4>Pick a category</h4>
            <div className="options">
              <label>
                <input
                  type="radio"
                  name="category"
                  id="category1"
                  value="business"
                  checked={this.state.category==="business"? true:false}
                  onChange={this.handleChangeCategory}
                />
                <span className="bubble business"></span>
                <div>Business</div>
              </label>

              <label>
                <input
                  type="radio"
                  name="category"
                  id="category2"
                  value="personal"
                  checked={this.state.category==="personal"? true:false}
                  onChange={this.handleChangeCategory}
                />
                <span className="bubble personal"></span>
                <div>Personal</div>
              </label>
            </div>

            <input type="submit" value="Add todo" />
          </form>
        </section>
      </div>
    );
  }
}
